import React, { useState, useCallback } from 'react';
//use callback allow us to save a function and this function should not be recreated with every execution
import Button from './components/UI/Button/Button'
import './App.css';
import DemoOutput from './components/Demo/DemoOutput';

function App() {

  const [showParagraph, setShowParagraph] = useState(false)
  const [allowToggle, setAllowToggle] = useState(false)

  const showParagraphHandler = useCallback(() => {
    if (allowToggle) {
      setShowParagraph(prevParagraph => !prevParagraph);
    }

  }, []);

  const allowToggleHandler = useCallback(() => {
    setAllowToggle(true);
  }, []);



  return (
    <div className="app">
      <h1>Hi there!</h1>
      <DemoOutput show={showParagraph} />
      <Button onClick={allowToggleHandler}>Allow Toggling</Button>
      <Button onClick={showParagraphHandler}>Toggle Paragraph!</Button>
    </div>
  );
}

export default App;
